rf_isreceiving()
{
  uint8_t r = (bucket_array[CC_INSTANCE][bucket_in[CC_INSTANCE]].state != STATE_RESET);
#ifdef HAS_FHT_80b
  r = (r || fht80b_timeout != FHT_TIMER_DISABLED);
#endif
  return r;
}

