uint8_t IRAM_ATTR makeavg(uint8_t i, uint8_t j)
{
  return (i+i+i+j)/4;
}

/*
 * Copy the data from bucket to receive buffer
 */
void copyData(uint8_t byteidx, uint8_t bitidx, uint8_t *data, uint8_t *obuf, uint8_t *oby, bool reverseBits) {
      uint8_t i;
      for (i=0;i<byteidx;i++) {
        if (reverseBits) {
          data[i] = ~data[i];
        }
        obuf[i]=data[i];
      }

      if (7-bitidx != 0) {
        if (reverseBits) {
          data[i] = ~data[i] & (0xFF<<(bitidx+1));
        }
        obuf[i]=data[i];
        i++;
      }
      *oby = i;
}


/*
 * Description in header
 */
void IRAM_ATTR addbit(bucket_t *b, uint8_t bit)
{
  if(b->byteidx>=sizeof(b->data)){
    reset_input();
  //  DC('f');
    return;
  }
  if(bit)
    b->data[b->byteidx] |= _BV(b->bitidx);

  if(b->bitidx-- == 0) {           // next byte
    b->bitidx = 7;
    b->data[++b->byteidx] = 0;
  }
  b->valCount = b->valCount + 1;

}

/*
 * Description in header
 */
uint8_t
getbit(input_t *in)
{
  uint8_t bit = (in->data[in->byte] & _BV(in->bit)) ? 1 : 0;
  if(in->bit-- == 0) {
    in->byte++;
    in->bit=7;
  }
  return bit;
}

/*
 * Description in header
 */
uint8_t
getbits(input_t* in, uint8_t nbits, uint8_t msb)
{
  uint8_t ret = 0, i;
  for (i = 0; i < nbits; i++) {
    if (getbit(in) )
      ret = ret | _BV( msb ? nbits-i-1 : i );
  }
  return ret;
}

